    width: 100%;
    height: auto;
    padding: 0;
}
[data-ttl=''],[data-desc='']{
    display: none !important;
} 
.wkit-hz-content-bg{
    position:relative;
    transition: .3s;
}
.wkit-multi-step-hz-wrapper .hz-dcl-1{
    width:100%;
}
.wkit-multi-step-hz-wrapper .hz-dcl-2{
    width:50%;
}
.wkit-multi-step-hz-wrapper .hz-dcl-3{
