.wkit-hz-card {
    position: relative;
    display: inline-block;
    width: 100%;
    height: auto;
    background-color: #ffffff;
    word-wrap: break-word;
}
.wkit-hz-card:after {
    content: "";
    border-radius: 5px;
    position: absolute;
    z-index: -1;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    opacity: 0;
    transition: all 0.4s cubic-bezier(0.165, 0.84, 0.44, 1);
}
.wkit-hz-step-content {
    position: relative;
    padding-right: 0;
    padding-left: 0;
    overflow:hidden;
    transition: .3s linear;
}
.wkit-hz-step-title,.wkit-hz-progress-icon,.wkit-hz-progress-img{
	display:none;
}
.wkit-multi-step-hz-wrapper .wkit-hz-text .wkit-hz-step-title{
    position: relative;
    display: inline-block;
    color:#000;
    padding-bottom: 6px;
    overflow:hidden;
    transition: .3s linear;
    margin-top: 20px;
}
.wkit-progress-area{
    margin-bottom: 16px; 
}
.wkit-hz-step-desc{
    position: relative;
    display: inline-block;
    color:#000;
    overflow:hidden;
    transition: .3s linear;
    margin-bottom: 20px;
}
.wkit-prg-one {
    background-size: 1536px 447px;
    background-position: -437.1px -1126.13px;
    background-attachment: scroll;
}
.wkit-prg-two {
    background-size: 1536px 447px;
    background-position: -422.1px -1115.13px;
    background-attachment: scroll;
}
.wkit-hz-active-yes .wkit-bg-gray-2 {
    background-color: #1818e8;
}
.wkit-bg-gray-2:hover {
    background-color: #cbd3da;
}
.wkit-hz-bg-dark-opacity-3 {
    background-color: rgba(0, 0, 0, 0.3);
}
.wkit-hz-bg-dark-opacity-3:hover {
    background-color: rgba(0, 0, 0, 0.3);
}
.wkit-shadow-hover-lg:hover {
    box-shadow: 0 0.5rem 1.2rem rgba(0, 0, 0, 0.1), 0 2rem 3rem rgba(0, 0, 0, 0.15);
}
.wkit-hz-bg-gradient-primary {
    background-color: #1818e8;
}
.wkit-pix-leveles-dot-div {
    position: relative;
    width: 30px;
    height: 30px;
    display: block;
    width: 100%;
    margin-top: -43px;
    margin-left: -15px;
}
.levels-dot-overlay{
    border-radius: 50%;
    position: absolute;
    width: 40px;
    height: 40px;
    display: inline-flex;
    align-items: center;
    justify-content: center;
    transition: all 0.4s cubic-bezier(0.165, 0.84, 0.44, 1);
}
.wkit-hz-pix-levels-dot {
    position: absolute;
    width: 30px;
    height: 30px;
    display: inline-block;
    text-align: center;
    border-radius: 50%;
    transition: all 0.4s cubic-bezier(0.165, 0.84, 0.44, 1);
    overflow: hidden;
}
.wkit-hz-pix-levels-dot-inner {
    width: 14px;
    height: 14px;
    border-radius: 50px;
    position: absolute;
    top: 8px;
    left: 8px;
    overflow: hidden;
}
.wkit-hz-pix-levels-step:hover .wkit-hz-pix-levels-dot, .wkit-hz-pix-levels-step:hover .levels-dot-overlay {
    transform: scale(1.25);
}
.wkit-hz-pix-levels-step .progress {
    position: relative;
    border-radius: 0;
    height: 8px;
    box-shadow: none;
    margin: 20px 0;
    width: 100%;
}
.wkit-hz-pix-levels-step .progress .progress-bar {
    width: 0;
    box-shadow: none;
}
.wkit-hz-pix-levels-step.wkit-hz-complete .progress .progress-bar {
    width: 100%;
}
.wkit-hz-pix-levels-step.wkit-hz-active-yes .progress .progress-bar {
    width: 50%;
}
.wkit-hz-pix-levels-step:first-child.wkit-hz-active-yes .progress .progress-bar {
    width: 0%;
}
.wkit-hz-pix-levels-step:last-child.wkit-hz-active-yes .progress .progress-bar {
    width: 100%;
}
.wkit-hz-pix-levels-step.disabled .wkit-hz-pix-levels-dot {
    background-color: #f5f5f5;
}
.wkit-hz-pix-levels-step.disabled .wkit-hz-pix-levels-dot:after {
    opacity: 0;
}
.wkit-hz-pix-levels-step:first-child .progress {
    left: 50%;
    width: 50%;
}
.wkit-hz-pix-levels-step:last-child .progress {
    width: 50%;
}
.wkit-hz-bg-gradient-primary {
    background-attachment: scroll;
}
.wkit-multi-step-hz-wrapper .wkit-hz-icon .wkit-hz-progress-icon {
    padding-bottom: 15px;
    display: inline-block;
    margin-top: 20px;
}
.wkit-multi-step-hz-wrapper .wkit-hz-image .wkit-hz-progress-img {
    display: flex;
    position: relative;
    justify-content: center;
    align-items: center;
    width: auto;
    height: 26%;
    margin-top: 11px;
}
.wkit-multi-step-hz-wrapper .wkit-hz-image .tp-hz-title-image {
    display: flex;
    position: relative;
    justify-content: center;
    align-items: center;
    width: 14%;
    height: auto;
}
.wkit-multi-step-hz-wrapper .wkit-hz-step-content.wkit-hz-icon .wkit-hz-progress-icon svg {
    display: inline-block;
    width: 25px;
    height: 20px;
}
.wkit-multi-step-hz-wrapper .wkit-hz-step-content.wkit-hz-icon .wkit-hz-progress-icon svg ,
.wkit-multi-step-hz-wrapper .wkit-hz-step-content.wkit-hz-icon .wkit-hz-progress-icon svg path,
.wkit-multi-step-hz-wrapper .wkit-hz-step-content.wkit-hz-icon .wkit-hz-progress-icon .tp-title-icon{
    transition: 0.3s linear;
}
.wkit-multi-step-hz-wrapper .wkit-hz-inner-style-2 .wkit-prg-two .wkit-hz-pix-levels-dot-inner {
    background: unset !important;
}
.wkit-multi-step-hz-wrapper .wkit-hz-inner-style-2 .wkit-prg-two:before{
    content: attr(data-index);
    position: relative;
    display: block;
    font-size: 16px;
    color: #fff;
    top: 1px;
    transition: .3s linear;
}
.wkit-multi-step-hz-wrapper .wkit-hz-style-icon{
    display: none;
}
.wkit-multi-step-hz-wrapper.wkit-hz-style-2 .wkit-hz-style-icon{
    position: relative;
    display: flex;
    justify-content: center;
    align-items: center;
    padding: 6px;
}
.wkit-multi-step-hz-wrapper .wkit-hz-inner-style-3 .wkit-hz-pix-levels-dot-inner.wkit-hz-check-mark:before {
    position: relative;
	display: block;
    font-size: 16px;
    font-family: 'Font Awesome 5 Free', 'FontAwesome';
    color: #fff;
    content: '\f00c';
    top: -4px;  
    transition: .3s linear;
}
.wkit-multi-step-hz-wrapper .wkit-hz-inner-style-3 .wkit-hz-pix-levels-dot-inner.wkit-hz-check-mark.wkit-hz-bg-dark-opacity-3 {
    background-color: unset;
}
.wkit-multi-step-hz-wrapper .wkit-hz-step-content.wkit-hz-pix-levels-step.wkit-hz-active-yes{
    opacity: 100%;
}

